import Axios from "axios"
import { PRODUCT_LIST_FAILED, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESSFUL } from "../constants/productConstants"

export const listProducts = () => async(dispatch) => {
    dispatch({
        type: PRODUCT_LIST_REQUEST
    })
    try {
        const {data} = await Axios.get("/api/products")
        dispatch({type: PRODUCT_LIST_SUCCESSFUL, payload: data})
    } catch(err) {
        dispatch({type: PRODUCT_LIST_FAILED, payload: err.message})
    }
}
