import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import Rating from "./Rating";

function Product(props) {
    const {product} = props
    const [qty, setQty] = useState(1)
    const productId = product._id

    const addToCartHandler = () => {
        props.history.push(`/cart/${productId}?qty=${qty}`)
    }
    return (
        <div key={product._id} className="card">
            <Link to={`/product/${product._id}`}>
                <img className="medium" src={product.image} alt={product.name} />
            </Link>
            <div className="card-body">
                <Link to={`/product/${product._id}`}>   
                    <h2>{product.name}</h2>
                </Link>
                <div className="card-details">
                    <Rating
                    rating = {product.rating}
                    numReviews = {product.numReviews}
                    />
                    <div className="price">${product.price}</div>
                </div>
                {
                        product.countInStock > 0 && (
                            <div className="product-button Onesp-top">   
                                <button onClick={addToCartHandler} className="add-to-cart thin">Add To Cart</button>
                                <span className="product-dropdown-menu">
                                    <h3>QTY</h3>
                                    <select className="product-dropdown" value={qty} onChange={e => setQty(e.target.value)}>
                                        {[...Array(product.countInStock).keys()].map(x => (
                                            <option key={x+1} value={x+1}>{x+1}</option>
                                        ))}
                                    </select>   
                                </span>
                            </div>      
                        )}
            </div>
        </div>
    )
}

export default Product
